INSERT INTO `wp_posts` VALUES("219", "1", "2015-02-02 11:34:49", "2015-02-02 11:34:49", "Хотел Будапеща предлага и допълнително оборудване и услуги свързани с успешното провеждане на събития:\r\n<ul>\r\n	<li>Аудио-визуална техника – проектор, микрофони, озвучаване.</li>\r\n	<li>Флипчарт и екрани.</li>\r\n	<li>Безплатна високоскоростна кабелна и безжична интернет връзка.</li>\r\n	<li>Изпращане и получаване на факсове.</li>\r\n	<li>Фото копиране.</li>\r\n	<li>Кафе-паузи.</li>\r\n	<li>Организиране на работни обеди и вечери.</li>\r\n	<li>Организиране на коктейли.</li>\r\n</ul>", "Допълнителни услуги", "", "publish", "closed", "open", "", "%d1%83%d1%81%d0%bb%d1%83%d0%b3%d0%b8", "", "", "2015-02-16 11:39:41", "2015-02-16 11:39:41", "", "71", "http://localhost/hotelbudapest/?page_id=219", "0", "page", "", "0");
