INSERT INTO `wp_posts` VALUES("505", "1", "2015-02-04 09:32:06", "2015-02-04 09:32:06", "Хотел Будапеща предлага и допълнително оборудване и услуги свързани с успешното провеждане на събития:\r\n<ul>\r\n	<li>Аудио-визуална техника – проектор, микрофони, озвучаване.</li>\r\n	<li>Флипчарт и екрани.</li>\r\n	<li>Безплатна високоскоростна кабелна и безжична интернет връзка.</li>\r\n	<li>Изпращане и получаване на факсове.</li>\r\n	<li>Фото копиране.</li>\r\n	<li>Кафе-паузи.</li>\r\n	<li>Организиране на работни обеди и вечери.</li>\r\n	<li>Организиране на коктейли.</li>\r\n</ul>", "Допълнителни услуги", "", "inherit", "open", "open", "", "219-revision-v1", "", "", "2015-02-04 09:32:06", "2015-02-04 09:32:06", "", "219", "http://localhost/hotelbudapest/219-revision-v1/", "0", "revision", "", "0");
