INSERT INTO `wp_posts` VALUES("518", "1", "2015-02-04 09:52:20", "2015-02-04 09:52:20", "<table class=\"menu\">\r\n<tbody>\r\n<tr>\r\n<th width=\"430px\">Салати</th>\r\n<th width=\"65px\"></th>\r\n<th width=\"80px\"></th>\r\n</tr>\r\n<tr>\r\n<td>Шопска салата с домати, краставици, чушки, пресен лук, краве сирене и магданоз</td>\r\n<td>250 гр</td>\r\n<td>7,00 лв</td>\r\n</tr>\r\n<tr class=\"odd\">\r\n<td>Свежи салати с круши, печени орехи, стафиди, лучен дресинг и пармезан</td>\r\n<td>250 гр</td>\r\n<td>8,00 лв</td>\r\n</tr>\r\n<tr>\r\n<td>Салата „Капрезе” с домати, моцарела, маслини и песто “Дженовезе”</td>\r\n<td>250 гр</td>\r\n<td>10,00 лв</td>\r\n</tr>\r\n<tr class=\"odd\">\r\n<td>Традиционна салата с печени чушки, фета сирене и брускети с доматена салца</td>\r\n<td>250 гр</td>\r\n<td>7,00 лв</td>\r\n</tr>\r\n<tr>\r\n<td>Пикантна салата с оризови нудли, свежи салати, сушени сливи, сотирани калмари и сладък чили сос</td>\r\n<td>250 гр</td>\r\n<td>10,00 лв</td>\r\n</tr>\r\n<tr class=\"odd\">\r\n<td>Лека Цезар салата</td>\r\n<td>250 гр</td>\r\n<td>9,00 лв</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table class=\"menu\">\r\n<tbody>\r\n<tr>\r\n<th width=\"430px\">Предястия</th>\r\n<th width=\"65px\"></th>\r\n<th width=\"80px\"></th>\r\n</tr>\r\n<tr>\r\n<td>Норвежка пушена сьомга с хрян, каперси и кромид лук, поднесена със зелена салата и запечен тост</td>\r\n<td>200 гр</td>\r\n<td>12,00 лв</td>\r\n</tr>\r\n<tr class=\"odd\">\r\n<td>Паниран камембер в корнфлейкс върху айсберг и сос от горски плодове</td>\r\n<td>200 гр</td>\r\n<td>12,00 лв</td>\r\n</tr>\r\n<tr>\r\n<td>Прошуто крудо с моцарела и смокиново сладко</td>\r\n<td>200 гр</td>\r\n<td>12,00 лв</td>\r\n</tr>\r\n<tr class=\"odd\">\r\n<td>Чимичанга с чеснов сос, телешко, ябълки, кимион, канела и репички</td>\r\n<td>200 гр</td>\r\n<td>10,00 лв</td>\r\n</tr>\r\n<tr>\r\n<td>Сухи традиционни мезета- луканка, суджук, пастърма и филе Елена</td>\r\n<td>300 гр</td>\r\n<td>14,00 лв</td>\r\n</tr>\r\n<tr class=\"odd\">\r\n<td>Ев