INSERT INTO `wp_posts` VALUES("1026", "1", "2015-03-19 10:06:38", "2015-03-19 10:06:38", "<h3 class=\"global-content-header\">Ресторант Будапеща</h3>\nРесторант Будапеща с капацитет от 50 места и предлага модерна и неангажираща обстановка и декор в пастелни, земни цветове и перфектното място за работен обяд или запомняща се романтична вечеря.\n\nИзисканото и <a href=\"http://www.hotelbudapest.bg/bg/restaurant/salads-and-cold-appetizers\">разнообразно меню</a> включва както специалитети инспирирани от световната кухня според сезона, така и класически български ястия и богата винена селекция.\n\nВ Ресторант Будапеща се сервира и богата закуска на бюфет за гостите на хотела. Професионалният персонал, спомага за успешното организиране на фирмени тържества, бизнес обеди и вечери.\n\nРаботното време на ресторанта е:\n<p class=\"whiteheading\">понеделник - петък</p>\n07:00 - 10,00 часа\n17:00 - 22:30 часа\n<p class=\"whiteheading\">събота - неделя</p>\n07:00  - 11:00 часа\n17:00 - 22:30 часа", "Ресторант Будапеща", "", "inherit", "open", "closed", "", "77-autosave-v1", "", "", "2015-03-19 10:06:38", "2015-03-19 10:06:38", "", "77", "http://localhost/hotelbudapest/77-autosave-v1/", "0", "revision", "", "0");
